jQuery(document).ready(function(){UMBERTO_STORAGE.media_frame=null;UMBERTO_STORAGE.media_link="";jQuery(".umberto_media_selector").on("click",function(a){umberto_show_media_manager(this);a.preventDefault();return false})});function umberto_show_media_manager(a){UMBERTO_STORAGE.media_link=jQuery(a);if(UMBERTO_STORAGE.media_frame){UMBERTO_STORAGE.media_frame.open();return false}UMBERTO_STORAGE.media_frame=wp.media({title:UMBERTO_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:UMBERTO_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:UMBERTO_STORAGE.media_link.data("update"),close:true}});UMBERTO_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+UMBERTO_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(UMBERTO_STORAGE.media_link.data("multiple")===true){UMBERTO_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=UMBERTO_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});UMBERTO_STORAGE.media_frame.open();return false};